import React from 'react';

class Children extends React.Component {
    state = {
        show: true,
    };
    onClickHidden = () => {
        this.setState({ show: false });
    };

    render() {
        const { listClass } = this.props;

        return (
            <div>
                <div>
                    <span
                        onClick={() => {
                            this.onClickHidden();
                        }}
                    >
                        {' '}
                        hidden
                    </span>
                </div>
                {this.state.show && (
                    <div>
                        {listClass.map((item) => {
                            return (
                                <div
                                    key={item.name}
                                    className={
                                        item.age > 18 ? 'red' : 'blueviolet'
                                    }
                                >
                                    <p>my name {item.name}</p>
                                    <p> age {item.age}</p>
                                </div>
                            );
                        })}
                    </div>
                )}
            </div>
        );
    }
}
export default Children;
