import React from 'react';

class AddInfo extends React.Component {
    state = {
        name: 'thái',
        age: 23,
    };
    onChangeInput = (even) => {
        this.setState({ name: even.target.value });
    };
    onClickSummit = (even) => {
        even.preventDefault();
        console.log(even);
        const a = this.props.handleAddNewUse;
        console.log(a);
    };
    render() {
        return (
            <div>
                <form onSubmit={(even) => this.onClickSummit(even)}>
                    <p>hôm nay là thứ 7 {this.state.name}</p>
                    <label htmlFor="">name</label>
                    <input
                        type="text"
                        onChange={(even) => this.onChangeInput(even)}
                        placeholder="tên"
                    />
                    <label htmlFor="">age</label>
                    <input
                        type="text"
                        onChange={(even) => this.onChangeInput(even)}
                        placeholder=" tuổi"
                    />
                    <button>thay đổi</button>
                </form>
            </div>
        );
    }
}

export default AddInfo;
