import React, { useState } from 'react';

const Children = ({ props }) => {
    const [show, setShow] = useState(true);

    const onClickHidden = () => {
        setShow(!show);
    };

    return (
        <div>
            <div>
                <span onClick={onClickHidden}> {show ? 'hidden' : 'show'}</span>
            </div>
            {show && (
                <div>
                    {props.listUser.map((item) => {
                        return (
                            <div
                                key={item.id}
                                className={item.age > 18 ? 'red' : 'blueviolet'}
                            >
                                <p>my name {item.name}</p>
                                <p> age {item.age}</p>
                                <button
                                    onClick={() => props.handleDelete(item.id)}
                                >
                                    detele
                                </button>
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
};
export default Children;
