import React, { useState } from 'react';

const AddInfo = (props) => {
    const [info, setInfo] = useState('');
    const onChangeName = (even) => {
        console.log(even.currentTarget.value);
        setInfo(even.currentTarget.value);
    };
    const onChangeAge = (even) => {
        setInfo(even.target.value);
    };
    const onClickSummit = (even) => {
        even.preventDefault();
        props.handleAddNewUser({
            id: Math.floor(Math.random() * 100 + 1) + '-random',
            name: info.name,
            age: info.age,
        });
        console.log(info.name);
        setInfo({
            name: '',
            age: '',
        });
    };

    return (
        <>
            <form onSubmit={onClickSummit}>
                <label htmlFor="">name</label>
                <input
                    type="text"
                    onChange={onChangeName}
                    placeholder="tên"
                    // value={info.name}
                />
                <label htmlFor="">age</label>
                <input
                    type="text"
                    onChange={onChangeAge}
                    placeholder=" tuổi"
                    // value={info.age}
                />
                <button>thay đổi</button>
            </form>
        </>
    );
};

export default AddInfo;
