import React from 'react';
import Children from './Children';

class MyComponent extends React.Component {
    state = {
        name: 'thái',
        age: 23,
    };
    onChangeInput = (even) => {
        this.setState({ name: even.target.value });
    };
    onClickSummit = (even) => {
        even.preventDefault();
        console.log(even);
    };
    listClass = {
        name: 'Nguyễn Văn A',
        age: 33,
    };
    render() {
        return (
            <div>
                <Children
                    useName={this.listClass.name}
                    useAge={this.listClass.age}
                />
                <form onSubmit={(even) => this.onClickSummit(even)}>
                    <p>hôm nay là thứ 7 {this.state.name}</p>
                    <input
                        type="text"
                        onChange={(even) => this.onChangeInput(even)}
                    />
                    <button>thay đổi</button>
                </form>
            </div>
        );
    }
}
export default MyComponent;
