import React from 'react';

class MyComponent extends React.Component {
    render() {
        const onChangeInput = (even) => {
            console.log(even.target.value);
        };
        const onChangeInpu = (event) => {
            console.log(event.target.value); // Phần tử <input> mà gây ra sự kiện
            console.log(event.currentTarget.value); // Phần tử mà hàm xử lý sự kiện được gắn kết
        };
        return (
            <div>
                <form>
                    <input
                        type="text"
                        onChange={(even) => onChangeInput(even)}
                    />
                    <button>thay đổi</button>
                    <div onChange={onChangeInpu}>
                        <input type="text" />
                        <button>Submit</button>
                    </div>
                </form>
            </div>
        );
    }
}
export default MyComponent;
