import React from 'react';
import AddInfo from './AddInfo';
import Children from './Children';

class MyComponent extends React.Component {
    state = {
        listUser: [
            { id: 1, name: 'Nguyễn Văn A', age: 16 },
            { id: 2, name: 'Nguyễn Văn V', age: 33 },
            { id: 3, name: 'Nguyễn Văn C', age: 33 },
        ],
    };
    handleAddNewUser = (a) => {
        console.log(a);
        this.setState({
            listUser: [a, ...this.state.listUser],
        });
    };
    handleDelete = (userid) => {
        console.log(userid);
        const clone = [...this.state.listUser];
        clone = clone.filter((item) => item.id !== userid);
        this.setState(clone);
    };
    render() {
        return (
            <>
                <AddInfo handleAddNewUser={this.handleAddNewUser} />
                <Children
                    listUser={this.state.listUser}
                    handleDelete={this.handleDelete}
                />
            </>
        );
    }
}
export default MyComponent;
