import React, { useState } from 'react';
import AddInfo from './AddInfo';
import Children from './Children';

const MyComponent = () => {
    const [list, setList] = useState([
        { id: 1, name: 'Nguyễn Văn A', age: 16 },
        { id: 2, name: 'Nguyễn Văn V', age: 33 },
        { id: 3, name: 'Nguyễn Văn C', age: 33 },
    ]);
    const handleAddNewUser = (a) => {
        console.log(a, 'a');
        setList([a, ...list]);
    };
    const handleDelete = (userid) => {
        console.log(userid);
        let clone = [...list];
        clone = clone.filter((item) => item.id !== userid);
        setList({ clone });
    };

    return (
        <>
            <AddInfo handleAddNewUser={handleAddNewUser} />
            <Children listUser={list} handleDelete={handleDelete} />
        </>
    );
};
export default MyComponent;
