import React, { useState } from 'react';
import './listStuden.css';

const ListStuden = () => {
    const [title, setTitle] = useState(['iD', 'Họ Và Tên', 'Tuổi', 'Email']);
    const [list, setList] = useState([
        {
            _id: 1,
            name: 'Nguyễn Văn A',
            age: 25,
            email: 'nguyenvana@gmail.com',
        },
        {
            _id: 2,
            name: ' Nguyễn Văn B ',
            age: 13,
            email: 'nguyenvanb@gamil.com',
        },
        {
            _id: 3,
            name: 'Nguyễn Văn C',
            age: 42,
            email: 'Nguyenvanc@gmail.com',
        },
        {
            _id: 4,
            name: 'Nguyễn Văn D',
            age: 23,
            email: 'nguyenvand@gmail.com',
        },
    ]);
    return (
        <div className="listStuden">
            <div>
                <input type="text" />
            </div>
            <table className="table">
                <tr className="title">
                    {title.map((item) => {
                        return <td key={item}>{item}</td>;
                    })}
                    <td>
                        {' '}
                        <button> thêm</button>
                    </td>
                </tr>
                {list.map((item) => {
                    return (
                        <tr className="list">
                            <td key={item._id}>{item._id}</td>
                            <td key={item.name}>{item.name}</td>
                            <td key={item.age}> {item.age} </td>
                            <td key={item.email}> {item.email} </td>
                            <td>
                                <button> chỉnh sửa</button>
                            </td>
                            <td>
                                <button>xoá</button>
                            </td>
                        </tr>
                    );
                })}
            </table>
        </div>
    );
};

export default ListStuden;
