import React, { useState } from 'react';

const Children = (props) => {
    const { listUser, handleDelete } = props;
    const [show, setShow] = useState(true);

    const onClickHidden = () => {
        setShow({ show: !show });
    };

    return (
        <div>
            <div>
                {/* {this.state.show ? (
                        <span onClick={this.onClickHidden}> hidden</span>
                    ) : (
                        <span onClick={this.onClickHidden}> show</span>
                    )} */}
                <span onClick={onClickHidden}>
                    {' '}
                    {this.state.show ? 'hidden' : 'show'}
                </span>
            </div>
            {show && (
                <div>
                    {listUser.map((item) => {
                        return (
                            <div
                                key={item.id}
                                className={item.age > 18 ? 'red' : 'blueviolet'}
                            >
                                <p>my name {item.name}</p>
                                <p> age {item.age}</p>
                                <button onClick={() => handleDelete(item.id)}>
                                    detele
                                </button>
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
};
export default Children;
